CREATE TABLE invasiv_plants (
    id SERIAL PRIMARY KEY,
    reference_name VARCHAR NULL,
    common_name VARCHAR NULL,
    class VARCHAR NULL,
    family VARCHAR NULL
);



-- Table for storing classification results
CREATE TABLE classification_results (
    id SERIAL PRIMARY KEY, 
    name TEXT NOT NULL,
    probability FLOAT NOT NULL,
    is_invasive BOOLEAN NOT NULL DEFAULT FALSE, 
    latitude DECIMAL(10, 6) NOT NULL, 
    longitude DECIMAL(10, 6) NOT NULL,
    img_url TEXT NOT NULL,
    family TEXT -- Added family column
);

-- Table for storing captured images
CREATE TABLE captured_images (
    id SERIAL PRIMARY KEY,
    image_data BYTEA NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    classification_result_id INTEGER REFERENCES classification_results(id)
);


